fn main() {
    let input = "
";
    let part1 = duplicate_checker(input, 4);
    println!("Part 1: {}", part1);

    let part2 = duplicate_checker(input, 14);
    println!("Part 2: {}", part2);
}

fn duplicate_checker(s: &str, i: u32) -> u32 {
    let mut output: u32 = i;
    let vector: Vec<char> = s.chars().collect();
    for a in 0..vector.len()-1 {
        let mut temp_val: Vec<char> = vector[a..a+i as usize].to_vec();
        temp_val.sort();
        temp_val.dedup();
        if temp_val.len() == i as usize {
            break;
        }
        output += 1;
    }
    return output;
}